mapper.select:
select count(DISTINCT edu_course.id) from edu_course left join edu_course_subject on edu_course.id=edu_course_subject.course_id left join sys_subject on edu_course_subject.SUBJECT_ID=sys_subject.SUBJECT_ID left join edu_course_profile on edu_course.id=edu_course_profile.course_Id left join edu_course_teacher on edu_course.id = edu_course_teacher.course_id
and edu_course.id in (select cm.course_id from edu_course_member as cm where cm.memberType_id = #{e.membertype})
and edu_course_teacher.teacher_id = #{e.teacherId}
and edu_course.name LIKE CONCAT('%',#{e.name},'%')
AND (edu_course_subject.subject_id=#{e.subjectId} or sys_subject.parent_id=#{e.subjectId})
and edu_course.id = #{e.id}
AND edu_course.sell_type!='LIVE'
AND edu_course.sell_type=#{e.sellType}
AND edu_course.isavaliable=#{e.isavaliable}
and edu_course.is_pay = #{e.isPay}
AND edu_course.live_begin_time>now()
AND edu_course.live_begin_time<now() and edu_course.live_end_time>now()
AND edu_course.live_end_time<now()

select DISTINCT edu_course.id, edu_course.name, edu_course.isavaliable, edu_course.addtime, edu_course.is_pay, edu_course.sourceprice, edu_course.currentprice, edu_course.title, edu_course.lessionnum, edu_course.coursetag, edu_course.logo, edu_course.mobile_logo, edu_course.update_time, edu_course.losetype, edu_course.lose_abs_time, edu_course.lose_time, edu_course.updateuser, edu_course.page_buycount, edu_course.page_viewcount, edu_course.freeurl, edu_course.sell_type, edu_course.live_begin_time, edu_course.live_end_time, sys_subject.SUBJECT_NAME, IFNULL(edu_course_profile.buycount,0) buycount, IFNULL(edu_course_profile.viewcount,0) viewcount, IFNULL(edu_course_profile.commentcount,0) commentcount, IFNULL(edu_course_profile.questiongcount,0) questiongcount, IFNULL(edu_course_profile.notecount,0) notecount, IFNULL(edu_course_profile.playcount,0) playcount from app_main_course left join edu_course on edu_course.id=app_main_course.course_id left join edu_course_subject on app_main_course.course_id=edu_course_subject.course_id left join sys_subject on edu_course_subject.SUBJECT_ID=sys_subject.SUBJECT_ID left join edu_course_profile on app_main_course.course_id=edu_course_profile.course_Id left join edu_course_teacher on app_main_course.course_id = edu_course_teacher.course_id
and edu_course.id in (select cm.course_id from edu_course_member as cm where cm.memberType_id = #{e.membertype})
and edu_course_teacher.teacher_id = #{e.teacherId}
and edu_course.name LIKE CONCAT('%',#{e.name},'%')
and edu_course.id = #{e.id}
AND (edu_course_subject.subject_id=#{e.subjectId} or sys_subject.parent_id=#{e.subjectId})
AND edu_course.sell_type!='LIVE'
AND edu_course.sell_type=#{e.sellType}
AND edu_course.isavaliable=#{e.isavaliable}
order by edu_course_profile.viewcount desc
order by edu_course.addTime desc
order by edu_course.currentPrice asc
order by edu_course.live_begin_time desc

