select edu_course.id, edu_course.name, edu_course.isavaliable, edu_course.addtime, edu_course.is_pay, edu_course.sourceprice, edu_course.currentprice, edu_course.title, edu_course.lessionnum, edu_course.coursetag, edu_course.logo, edu_course.mobile_logo, edu_course.update_time, edu_course.losetype, edu_course.lose_abs_time, edu_course.lose_time, edu_course.updateuser, edu_course.page_buycount, edu_course.page_viewcount, edu_course.freeurl, edu_course.sell_type, edu_course.live_begin_time, edu_course.live_end_time, edu_website_course_detail.recommendId, IFNULL(edu_course_profile.buycount,0) buycount, IFNULL(edu_course_profile.viewcount,0) viewcount, IFNULL(edu_course_profile.commentcount,0) commentcount, IFNULL(edu_course_profile.questiongcount,0) questiongcount, IFNULL(edu_course_profile.notecount,0) notecount, IFNULL(edu_course_profile.playcount,0) playcount from edu_website_course_detail join edu_course on edu_website_course_detail.course_id=edu_course.id left join edu_course_profile on edu_course.id=edu_course_profile.course_Id
edu_website_course_detail.recommendId=#{value}
order by edu_website_course_detail.recommendId,edu_website_course_detail.orderNum desc,edu_website_course_detail.id

