mapper.select:
SELECT EDU_TEACHER.NAME FROM EDU_TEACHER
LEFT JOIN EDU_COURSE_TEACHER ON EDU_COURSE_TEACHER.TEACHER_ID = EDU_TEACHER.ID
WHERE EDU_COURSE_TEACHER.COURSE_ID=#{value}

 		SELECT SYS_SUBJECT.SUBJECT_NAME FROM SYS_SUBJECT
		LEFT JOIN EDU_COURSE_SUBJECT ON EDU_COURSE_SUBJECT.SUBJECT_ID = SYS_SUBJECT.SUBJECT_ID
		WHERE EDU_COURSE_SUBJECT.COURSE_ID=#{value}

select

from user_user
where user_user.email = #{email}

select count(1)
from user_user
where user_user.mobile =#{mobile}

select count(1) from edu_article
edu_article.type=#{e.type}
and edu_article.title like CONCAT('%',#{e.title},'%')

select , EDU_COURSE_KPOINT.videourl, EDU_COURSE_KPOINT.videotype FROM EDU_COURSE_STUDYHISTORY LEFT JOIN EDU_COURSE ON EDU_COURSE.ID = EDU_COURSE_STUDYHISTORY.COURSE_ID LEFT JOIN EDU_COURSE_KPOINT ON EDU_COURSE_STUDYHISTORY.KPOINT_ID= EDU_COURSE_KPOINT.ID LEFT JOIN EDU_TEACHER ON EDU_TEACHER.ID = EDU_COURSE_KPOINT.TEACHER_ID WHERE EDU_COURSE.SELL_TYPE!='LIVE'
AND EDU_COURSE_STUDYHISTORY.USER_ID = #{e.userId}
ORDER BY EDU_COURSE_STUDYHISTORY.ID DESC

mapper.update:
update app_webstie_use SET
end_time = #{endTime}
where
brand = #{brand}
and type = #{type}
and size = #{size}
and user_id = #{userId}
and end_time is null

mapper.select:
SELECT EDU_COURSE.ID AS courseId, EDU_COURSE.ID AS id, EDU_COURSE.NAME as name, EDU_COURSE.LOGO as logo, EDU_COURSE.sourcePrice as sourcePrice, EDU_COURSE.currentPrice as currentPrice, EDU_COURSE.LESSIONNUM as lessionnum, EDU_COURSE.PAGE_VIEWCOUNT as pageViewcount FROM APP_MAIN_COURSE LEFT JOIN EDU_COURSE ON EDU_COURSE.ID=APP_MAIN_COURSE.COURSE_ID LEFT JOIN EDU_COURSE_SUBJECT ON EDU_COURSE_SUBJECT.COURSE_ID = EDU_COURSE.ID WHERE ISAVALIABLE=0
AND EDU_COURSE_SUBJECT.SUBJECT_ID=#{subjectId}
AND EDU_COURSE.ID!=#{courseId}
limit #{num}
