mapper.select:
select

,
edu_card.type
from edu_card_code left join
`edu_card` on edu_card.id =
edu_card_code.card_id


		and edu_card_code.card_code =#{cardCode}


		and edu_card_code.card_code_password =#{cardCodePassword}


		and edu_card_code.card_id = #{cardId}

SELECT edu_card_code.id, edu_card_code.`card_code`, edu_card_code.`card_code_password`, edu_card_code.`user_email`, edu_card_code.`create_time`, edu_card_code.`use_time`, edu_card.`type`, edu_card.`remark` FROM `edu_card_code` LEFT JOIN `edu_card` ON edu_card_code.`card_id` = edu_card.`id`
and edu_card.type = #${e.type}
and edu_card_code.user_email =#${e.user_email}
and edu_card.name= #${e.name}
and edu_card.create_user =#${e.create_user}
and edu_card_code.card_code =#${e.card_code}

SELECT count(*)
FROM
`edu_card_code` LEFT JOIN `edu_card`
ON
edu_card_code.`card_id` = edu_card.`id`


	and 	edu_card.type = #${e.type}


		and edu_card_code.user_email =#${e.user_email}


		and edu_card.name= #${e.name}


		and edu_card.create_user =#${e.create_user}



		and edu_card_code.card_code =#${e.card_code}

mapper.update:
update edu_card_code set edu_card_code.status = 'CLOSE' where edu_card_code.id in (
#{item.id}
)

mapper.select:
select edu_card.id, edu_card.name, edu_card.money, edu_card.type, edu_card.num, edu_card.begin_time, edu_card.end_time, edu_card.create_user, edu_card.create_time, edu_card.course_name from edu_card
and edu_card.type = #{e.type}
and `edu_card`.create_time>=CONCAT(#{e.beginTime},'00:00:00')
and `edu_card`.create_time<=CONCAT(#{e.endTime},'23:59:59')
and edu_card.course_name like #{e.courseName}
and edu_card.create_user like #{e.createUser}
and edu_card.name like #{e.cardName}
order by edu_card.id desc

select 
count(1)
from edu_card


		and edu_card.type = #{e.type}



		and  `edu_card`.create_time>=CONCAT(#{e.beginTime},'00:00:00')


		and  `edu_card`.create_time<=CONCAT(#{e.endTime},'23:59:59')



		and edu_card.course_name like #{e.courseName}


		and edu_card.create_user like #{e.createUser}


		and edu_card.name like #{e.cardName}

SELECT edu_card_code.user_email, edu_card_code.id, edu_card_code.trxorder_id, edu_card_code.request_id, edu_card_code.card_code, edu_card_code.card_code_password, edu_card_code.use_time, edu_card_code.status, ifnull(edu_card.money,0) as money , edu_card.end_time, edu_card.name, edu_card.type, edu_card.remark, edu_card.create_user, edu_card.begin_time, edu_card.course_name, edu_card.create_time FROM edu_card_code LEFT JOIN edu_card ON edu_card.`id` = edu_card_code.`card_id`
and edu_card_code.`user_id` = #{e.userId}
and edu_card.name like #{e.name}
and `edu_card`.create_time>=#{e.beginTime}
and `edu_card`.create_time<=#{e.endTime}
and `edu_card`.type=#{e.type}
and `edu_card_code`.status=#{e.status}
and edu_card.create_user =#{e.createUser}
and edu_card_code.card_code like #{e.cardNode}
and edu_card_code.user_email like #{e.email}
and `edu_card_code`.card_id =#{e.cardId}
order by edu_card_code.id desc

SELECT
count(1)
from edu_card_code
LEFT JOIN edu_card ON
edu_card.`id` =
edu_card_code.`card_id`


	    and edu_card_code.`user_id` = #{e.userId}


		and edu_card.name like #{e.name}


		and  `edu_card`.create_time>=#{e.beginTime}


		and  `edu_card`.create_time<=#{e.endTime}


		and  `edu_card`.type=#{e.type}


		and  `edu_card_code`.status=#{e.status}


		and  edu_card.create_user =#{e.createUser}


		and  edu_card_code.card_code like #{e.cardNode}


		and  edu_card_code.user_email like #{e.email}


		and	`edu_card_code`.card_id  =#{e.cardId}
