mapper.select:
select DISTINCT edu_course.id, edu_course.name, edu_course.isavaliable, edu_course.addtime, edu_course.is_pay, edu_course.sourceprice, edu_course.currentprice, edu_course.title, edu_course.lessionnum, edu_course.total_lessionnum, edu_course.minutes, edu_course.coursetag, edu_course.logo, edu_course.update_time, edu_course.losetype, edu_course.lose_abs_time, edu_course.lose_time, edu_course.updateuser, edu_course.page_buycount, edu_course.page_viewcount, edu_course.freeurl, edu_course.sell_type, edu_course.live_begin_time, edu_course.live_end_time, edu_course.play_time, IFNULL(edu_course_profile.buycount,0) buycount, IFNULL(edu_course_profile.viewcount,0) viewcount, IFNULL(edu_course_profile.commentcount,0) commentcount, IFNULL(edu_course_profile.questiongcount,0) questiongcount, IFNULL(edu_course_profile.notecount,0) notecount, IFNULL(edu_course_profile.playcount,0) playcount, IFNULL(edu_course_profile.watchpersoncount,0) watchpersoncount,
IF(NOW()>=edu_course.live_begin_time AND NOW()<=edu_course.live_end_time,0,1) as live_status,
sys_subject.`SUBJECT_NAME` from edu_course left join edu_course_subject on edu_course.id=edu_course_subject.course_id left join sys_subject on edu_course_subject.SUBJECT_ID=sys_subject.SUBJECT_ID left join edu_course_profile on edu_course.id=edu_course_profile.course_Id left join edu_course_teacher on edu_course.id = edu_course_teacher.course_id left join group_middle_course on group_middle_course.course_id = edu_course.id LEFT JOIN edu_teacher ON edu_teacher.`id` = edu_course_teacher.`teacher_id`
AND edu_course.company_sell_type != 'INNER'
AND edu_course.company_sell_type = #{e.companySellType}
and group_middle_course.group_id = #{e.groupId}
and edu_course.id in (select cm.course_id from edu_course_member as cm where cm.memberType_id = #{e.membertype})
and edu_course_teacher.teacher_id = #{e.teacherId}
and edu_course.name LIKE CONCAT('%',#{e.name},'%')
and edu_course.id = #{e.id}
AND (edu_course_subject.subject_id=#{e.subjectId} or sys_subject.parent_id=#{e.subjectId})
AND edu_course.sell_type!='LIVE'
AND edu_course.sell_type!='PACKAGE'
AND edu_course.sell_type=#{e.sellType}
AND edu_course.`type_subject_id`=#{e.typeSubjectId}
AND edu_course.isavaliable=#{e.isavaliable}
and edu_course.is_pay = #{e.isPay}
and edu_course.class_type_id = #{e.classTypeSubjectId}
and edu_course.sys_user_id = #{e.sysUserId}
AND edu_course.live_begin_time>now()
AND edu_course.live_begin_time<now() and edu_course.live_end_time>now()
AND edu_course.live_end_time<now()
group by edu_course.id
ORDER BY live_status,edu_course.live_begin_time DESC
order by edu_course.order desc,edu_course_profile.viewcount desc
order by edu_course.order desc,edu_course.addTime desc
order by edu_course.currentPrice asc
order by CASE WHEN edu_course.live_end_time<now() THEN date_add(edu_course.live_begin_time, interval (36500+(datediff(DATE_FORMAT(now(),'%Y%m%d'),DATE_FORMAT(edu_course.live_begin_time,'%Y%m%d')))*2) day) ELSE edu_course.live_begin_time END ASC

